.header__top {
  display: flex;
  justify-content: space-between;
}
.header .logo {
  position: absolute;
  top: 2em;
  left: 1.5em;
  z-index: 1000;
  transition: opacity 0.5s ease;
}
.header .logo[data-visible=false] {
  opacity: 0;
}
.header__box {
  border: 2px solid #fff;
  padding: 1.25em;
  color: var(--clr-primary-100);
  line-height: 1;
  font-size: min(5vw, 2.1rem);
  max-width: fit-content;
  justify-self: center;
}

.nav-toggle {
  display: block;
  cursor: pointer;
  border: 0;
  background: none;
  position: absolute;
  top: 2.4em;
  right: 1.5em;
  z-index: 1000;
}

